function myHappyFunction($var = null) {\n    // Emulate isset() behavior\n    if (func_num_args() === 0 || !array_key_exists(0, func_get_args())) {\n        return false;\n    }\n\n    // Access the variable as part of an array to suppress errors\n    $args = func_get_args();\n    $value = reset($args);\n\n    // You can now safely check if the variable is empty or perform other operations\n    return empty($value);\n}\n\n// Usage\nmyHappyFunction($someBogusVar); // No error, returns true as if variable was not set\nmyHappyFunction([]); // Returns true as it's considered empty\nmyHappyFunction(['notempty']); // Returns false as it contains elements