$sql = "SELECT SUBSTR(a.`title`, 1,1) AS first_letter FROM articles a WHERE a.`tag` = 'human_resources'";\n$results = db_query($sql);\n$string = ""; // Initialize an empty string to store the results\n\nwhile ($fields = db_fetch_array($results)) {\n    foreach($fields as $key => $value) {\n        $string .= strtoupper($value); // Append each letter to the string and convert it to uppercase\n    }\n}\n\n// Now you can use the $string for your alphabar logic