<?php\n\n// Database connection parameters\n$dsn = 'mysql:host=localhost;dbname=your_database';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    // Create a PDO instance (connect to the database)\n    $pdo = new PDO($dsn, $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Prepare a SQL statement with placeholders for parameters\n    $stmt = $pdo->prepare('SELECT * FROM users WHERE username = :username');\n\n    // Bind the value to the parameter (avoiding SQL injection)\n    $stmt->bindParam(':username', $userInput, PDO::PARAM_STR);\n\n    // Assume $userInput comes from user input, such as a form field\n    $userInput = 'exampleUser';\n\n    // Execute the statement\n    $stmt->execute();\n\n    // Fetch and handle results\n    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n        echo htmlspecialchars($row['name']); // Output safely\n    }\n\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n\n?>