function schoolDD($name, $selected) {\n    $select = '';\n    if ($selected != null) {\n        $select = $selected;\n    }\n\n    // Corrected SQL query syntax\n    $qry = "SELECT * \n            FROM school \n            WHERE display = 'Y'\n            ORDER BY name, id";\n\n    $schools = _execQry($qry);\n\n    $html = '<select name="' . htmlspecialchars($name) . '">';\n\n    foreach ($schools as $s) {\n        $html .= '<option value="' . htmlspecialchars($s['id']) . '"';\n        if ($select == $s['id']) { // Assuming $selected should match the school id\n            $html .= ' selected="selected"';\n        }\n        $html .= '>' . htmlspecialchars($s['name']) . '</option>';\n    }\n\n    $html .= '</select>';\n    return $html;\n}