<?php\n\n$filename = 'yourfile.txt';\n$filePointer = fopen($filename, 'r');\n\nif (!$filePointer) {\n    die('Could not open file.');\n}\n\nfseek($filePointer, 0, SEEK_END); // Move to the end of the file\n\nwhile (true) {\n    $read = [$filePointer];\n    $null = null; // This will be for write changes\n    $null2= null; // And this for exceptions\n\n    // Wait until a change happens within your file pointer\n    if (!stream_select($read, $null, $null2, 1)) {\n        continue;\n    }\n\n    while (($line = fgets($filePointer)) !== false) {\n        echo $line . "<br>";\n    }\n}\n\nfclose($filePointer);\n\n?>