// Assuming you have already started your session with session_start();\n$mysqli = new mysqli("host", "user", "password", "database");\n\nif ($mysqli->connect_error) {\n    die('Connect Error (' . $mysqli->connect_errno . ') ' . $mysqli->connect_error);\n}\n\n$stmt = $mysqli->prepare("SELECT id, fName, lName FROM users WHERE username=? AND password=?");\n$username = $_POST['username'];\n$password = md5($_POST['password']); // Consider using a better hashing method like bcrypt\n\n$stmt->bind_param("ss", $username, $password);\n$stmt->execute();\n$result = $stmt->get_result();\n\nif ($result->num_rows < 1) {\n    redirect('index.php?p=signup');\n}\n\n$userData = $result->fetch_assoc();\n$_SESSION['id'] = $userData['id'];\n$_SESSION['fName'] = $userData['fName'];\n$_SESSION['lName'] = $userData['lName'];\n\n$stmt->close();\n$mysqli->close();