<?php\n\nfunction doesGravatarExist($email) {\n    // Generate the MD5 hash of the email address\n    $hash = md5(strtolower(trim($email)));\n    \n    // Create the URL with d=404 parameter\n    $uri = "https://www.gravatar.com/avatar/" . $hash . "?d=404";\n    \n    // Get headers\n    $headers = @get_headers($uri);\n    \n    // Check if the first header contains '200'\n    return strpos($headers[0], "200 OK") !== false;\n}\n\n// Example usage:\n$email = 'user@example.com';\nif (doesGravatarExist($email)) {\n    echo "Valid Gravatar exists.";\n} else {\n    echo "No valid Gravatar found.";\n}\n\n?>