class Foo {\n    public function bar() {\n        // Get the backtrace\n        $backtrace = debug_backtrace(DEBUG_BACKTRACE_IGNORE_ARGS);\n\n        // Find the first entry in the backtrace that is not this method call\n        foreach ($backtrace as $entry) {\n            if (isset($entry['file']) && basename($entry['file']) !== 'Foo.class.php') {\n                // Return the directory of the original file\n                return dirname($entry['file']);\n            }\n        }\n\n        // Fallback, should not reach here if everything is correct\n        return null;\n    }\n}\n\n// In index.php\nrequire_once 'inc/app/Foo.class.php';\n\n$foo = new Foo();\necho $foo->bar(); // Outputs: /home/user/public/www