<?php\n// Turn off compression if it's on, as it will interfere with real-time output\nini_set('zlib.output_compression', 'Off');\n@apache_setenv('no-gzip', 1);\n\nheader('Content-Type: text/html; charset=utf-8');\n\n// Disable output buffering\nob_implicit_flush(true);\nob_end_flush();\n\n// Example of printing data in real time\nfor ($i = 0; $i < 5; $i++) {\n    echo "Echo number: " . $i . "<br>";\n    \n    // Flush the buffer to ensure data is sent immediately\n    flush();\n    \n    // Sleep for a second to simulate processing time\n    sleep(1);\n}\n?>