<?php\n\nclass hello {\n    var $greeting = "hello";\n\n    function __construct($instanceName) {\n        echo $this->greeting;\n        return;\n    }\n}\n\n// Maintain an associative array of instance names and their objects\n$instances = [\n    'hello1' => new hello('hello1'),\n    'hello2' => new hello('hello2'),\n    'hello4' => new hello('hello4')\n];\n\n// To get the association of variable name to class\n$instanceClasses = [];\nforeach ($instances as $name => $instance) {\n    $instanceClasses[$name] = get_class($instance);\n}\n\nprint_r($instanceClasses);\n\n?>