<?php\n$url = 'http://www.google.com';\nif (array_key_exists('_check', $_POST)) {\n    // Sanitize the URL input\n    $url = filter_var($_POST['url'], FILTER_SANITIZE_URL);\n    \n    // Validate and restrict URLs to specific domains, e.g., example.com and www.example.com\n    $allowed_domains = ['example.com', 'www.example.com'];\n    $parsed_url = parse_url($url);\n    if (!in_array($parsed_url['host'], $allowed_domains)) {\n        die('URL is not allowed.');\n    }\n}\n\n// Fetch the title from the selected page\n$file = @ fopen(($url),"r") or die ("Can't read input stream");\n$text = fread($file,16384);\nif (preg_match('/<title>(.*?)<\/title>/is',$text,$found)) {\n        $title = htmlspecialchars(strip_tags($found[1]));\n} else {\n        $title = "Untitled Document";\n}\n?>