function isArraySequential(array $array) {\n    // Check if the array is empty\n    if (empty($array)) {\n        return true; // Assuming an empty array is considered sequential\n    }\n\n    // Get the keys of the array\n    $keys = array_keys($array);\n\n    // Check if all keys are integers and start from 0\n    foreach ($keys as $index => $key) {\n        if (!is_int($key) || $key !== $index) {\n            return false; // If any key is not an integer or not in sequence, it's associative\n        }\n    }\n\n    return true; // All keys are integers and in sequence\n}\n\n$sequentialArray = ['apple', 'orange', 'tomato', 'carrot'];\n$assocArray = ['fruit1' => 'apple', 'fruit2' => 'orange', 'veg1' => 'tomato', 'veg2' => 'carrot'];\n\nvar_dump(isArraySequential($sequentialArray)); // Outputs: bool(true)\nvar_dump(isArraySequential($assocArray));     // Outputs: bool(false)