class DAL {\n    protected $username;\n    protected $pwd;\n    protected $host;\n    protected $dbname;\n    protected $pdo;  // Use PDO instead of a direct connection link\n\n    public function __construct() {\n        $this->username = "your_username";\n        $this->pwd = "your_password";\n        $this->host = "localhost";  // Or your host\n        $this->dbname = "your_database";\n    }\n\n    public function connect() {\n        try {\n            $dsn = "mysql:host=" . $this->host . ";dbname=" . $this->dbname;\n            $this->pdo = new PDO($dsn, $this->username, $this->pwd);\n            // Set the PDO error mode to exception\n            $this->pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n        } catch (PDOException $e) {\n            echo "Connection failed: " . $e->getMessage();\n        }\n    }\n\n    protected function query($sql) {\n        return $this->pdo->query($sql);\n    }\n\n    public function getAllCountries() {\n        $sql = "SELECT id, name FROM country";\n        return $this->query($sql);\n    }\n}\n\nclass myOtherClass extends DAL {\n    public function __construct() {\n        parent::__construct();\n        parent::connect();\n    }\n\n    public function getCountryListBox() {\n        $result = parent::getAllCountries();\n\n        if ($result) {\n            $selectbox = "<select id='countryListBox' name='countryListBox'>";\n            while ($row = $result->fetch(PDO::FETCH_ASSOC)) {  // Use PDO fetch method\n                $selectbox .= "<option value='" . htmlspecialchars($row['id']) . "'>" . htmlspecialchars($row['name']) . "</option>";\n            }\n            $selectbox .= "</select>";\n        } else {\n            $selectbox = "Countries could not be retrieved from database.";\n        }\n\n        return $selectbox;\n    }\n}\n\n$sl = new myOtherClass();\necho '<form id="label_data_form">';\necho $sl->getCountryListBox();  // Echo the result to display it\necho '</form>';