// Include Zend Framework\nrequire_once 'Zend/Pdf.php';\n\n// Create new PDF document\n$pdf = new Zend_Pdf();\n\n// Add a new page\n$page1 = $pdf->newPage(Zend_Pdf_Page::SIZE_A4);\n$pdf->pages[] = $page1;\n\n// Load custom font (make sure to use a TTF that supports the needed characters)\n$fontPath = '/path/to/your/font.ttf'; // Ensure this is the correct path\ntry {\n    $unicodeFont = Zend_Pdf_Font::fontWithPath($fontPath);\n} catch (Zend_Exception $e) {\n    echo "Error loading font: " . $e->getMessage();\n    exit;\n}\n\n// Set font and size for page 1\n$page1->setFont($unicodeFont, 24);\n\n// Draw text using UTF-8 encoding\n$page1->drawText('aąbcčdeę', 72, 720, 'UTF-8');\n\n// Save the PDF document to a file or send it directly to the browser\n$pdf->save('/path/to/output.pdf');