$allowedMethods = ['methodOne', 'methodTwo']; // Define safe methods\n\nif (isset($_GET['func']) && in_array($_GET['func'], $allowedMethods)) {\n    $methodName = $_GET['func'];\n} else {\n    die('Invalid method.');\n}\n\nif (isset($_GET['param'])) {\n    $param = filter_input(INPUT_GET, 'param', FILTER_SANITIZE_STRING);\n} else {\n    die('Missing parameter.');\n}\n\n$obj = new myClass();\n$result = $obj->$methodName($param);