$connect    = ftp_connect('example.com');\n$result     = ftp_login($connect, 'username', 'password');\n\nif (!$result) {\n    echo "FTP connection has failed!";\n    exit;\n}\n\necho '<pre>';\n$directories = [\n    '' => 'Root Directory',\n    '/outgoing/' => 'Outgoing Directory',\n    '/2689312/' => 'Directory 2689312',\n    '/2689312/outgoing/' => 'Directory 2689312 Outgoing'\n];\n\nforeach ($directories as $path => $description) {\n    $list = ftp_nlist($connect, $path);\n    if ($list === false) {\n        echo "Failed to list contents of {$description} ({$path})\n";\n    } else {\n        echo "Contents of {$description} ({$path}):\n";\n        print_r($list);\n    }\n}\n\nftp_close($connect);