function cacheWrite($id, $data) {\n    // Create a hash from the ID to distribute files across directories\n    $hash = md5($id);\n    // Use first few characters of hash as directory name\n    $dirPath = "../cache/" . substr($hash, 0, 2) . "/" . substr($hash, 2, 2) . "/";\n    \n    if (!is_dir($dirPath)) {\n        mkdir($dirPath, 0755, true);\n    }\n    \n    $filePath = $dirPath .  $id . ".htm";\n    file_put_contents($filePath, $data);\n}\n\n// Usage\n$id = bin2hex(random_bytes(16)); // Generate a random ID\n$temp = "<html><body>Cache Data</body></html>";\ncacheWrite($id, $temp);