<?php\n\n$scripts = [\n    'script1.php',\n    'script2.php',\n    'script3.php'\n];\n\nforeach ($scripts as $script) {\n    // Define the command to run the script\n    $command = 'C:\Path\To\Php.exe ' . escapeshellarg($script);\n\n    // Open process with custom pipes\n    $descriptorspec = array(\n       0 => array("pipe", "r"),  // stdin is a pipe that the child will read from\n       1 => array("pipe", "w"),  // stdout is a pipe that the child will write to\n       2 => array("pipe", "w")   // stderr is a pipe that the child will write to\n    );\n\n    $process = proc_open($command, $descriptorspec, $pipes);\n\n    if (is_resource($process)) {\n        // Close stdin so PHP doesn't hang waiting for input from it\n        fclose($pipes[0]);\n\n        // Fetch output and error output\n        $output = stream_get_contents($pipes[1]);\n        $errors = stream_get_contents($pipes[2]);\n\n        fclose($pipes[1]);\n        fclose($pipes[2]);\n\n        // Close the process and check its return value\n        $return_value = proc_close($process);\n\n        echo "Script $script Output:\n$output\n";\n        echo "Script $script Errors:\n$errors\n";\n    }\n}