function containsTrueValue($array) {\n    foreach ($array as $value) {\n        // Check if $value is an array and if it contains a true value, recurse.\n        if (is_array($value)) {\n            if (containsTrueValue($value)) {\n                return true;\n            }\n        } else { // If not an array, check the boolean value.\n            if ((bool) $value === true) {\n                return true;\n            }\n        }\n    }\n    return false; // Return false if no true values are found by the end of the loop.\n}\n\n// Test cases\n$array1 = array(\n    'test' => array(0, 0, 0, array(0, 0, 0))\n);\n$array2 = array(\n    'test' => array(0, 0, 0, array(6, 0, 0)) // notice the 6\n);\n\n// Should return false.\nvar_dump(containsTrueValue($array1));\n\n// Should return true.\nvar_dump(containsTrueValue($array2));