<?php\n\n$myArray = [\n    'name' => 'juank',\n    'age' => 26,\n    'config' => ['usertype' => 'admin', 'etc' => 'bla bla']\n];\n\nfunction updateArray(&$array, $key, $value) {\n    // Check if the key is 'config'\n    if ($key === 'config') {\n        trigger_error("Modification of 'config' key is not allowed.", E_USER_ERROR);\n        return;\n    }\n    \n    // Update the array if the key is not 'config'\n    $array[$key] = $value;\n}\n\n// Example usage:\nupdateArray($myArray, 'name', 'Juan Karter');\nprint_r($myArray);\n\n// This will trigger an error\nupdateArray($myArray, 'config', ['usertype' => 'user']);\n\n?>