#!/usr/bin/env python3\n\nimport mysql.connector\n\nprint("Content-Type: text/html")\nprint()\n\nprint("<html><head><title>Books</title></head>")\nprint("<body>") \nprint("<h1>Books</h1>") \nprint("<ul>")\n\nconnection = mysql.connector.connect(user='me', password='letmein', database='my_db')\ncursor = connection.cursor()\nquery = "SELECT name FROM books ORDER BY pub_date DESC LIMIT 10"\ncursor.execute(query)\n\nfor row in cursor.fetchall():\n    print(f"<li>{row[0]}</li>")\n\nprint("</ul>") \nprint("</body></html>")\nconnection.close()