# -*- coding: utf-8 -*-\nimport os\n\n# Print current PATH for debugging\nprint(os.environ['PATH'])\n\n# Ensure the Graphviz bin directory is in PATH\ngraphviz_path = '/usr/local/bin'  # Update this path based on `which dot`\nif graphviz_path not in os.environ['PATH']:\n    os.environ['PATH'] += ':' + graphviz_path\n\n# Verify path update\nprint(os.environ['PATH'])\n\n# Test with ls command\nprint(os.system("ls >> a.txt"))\n\n# Run dot command\nprint(os.system(f"{graphviz_path}/dot -o9.png -Tpng ./6.dot"))