# Python example using ZeroMQ with CurveZMQ for secure messaging\n    import zmq.auth\n\n    ctx = zmq.Context()\n\n    server_sk, client_pk = zmq.curve_keypair()\n    server_pk, client_sk = zmq.curve_keypair()\n\n    server = ctx.socket(zmq.REP)\n    server.setsockopt_string(zmq.CURVE_SERVER, '1')\n    server.setsockopt_string(zmq.CURVE_PUBLICKEY, server_pk)\n    server.setsockopt_string(zmq.CURVE_SECRETKEY, server_sk)\n\n    client = ctx.socket(zmq.REQ)\n    client.setsockopt_string(zmq.CURVE_SERVERKEY, server_pk)\n    client.setsockopt_string(zmq.CURVE_PUBLICKEY, client_pk)\n    client.setsockopt_string(zmq.CURVE_SECRETKEY, client_sk)