# Define a callback for the Proceed button that checks which radio button is selected.\ndef proceed(self, widget):\n    if self.updatePageRadio.get_active():\n        self.updatePage()\n    elif self.updatePostRadio.get_active():\n        self.updatePost()\n    elif self.pageRadio.get_active():\n        self.newPage()\n    elif self.blogRadio.get_active():\n        self.newBlogPost()\n    elif self.addSpaceRadio.get_active():\n        self.newSpace()\n    elif self.removePageRadio.get_active():\n        self.removePage()\n    elif self.removePostRadio.get_active():\n        self.removeBlogPost()\n    elif self.removeSpaceRadio.get_active():\n        self.removeSpace()\n\n# Connect the Proceed button to the proceed method.\nself.contentProceedButton.connect("clicked", self.proceed)