# Example Python code snippet using SQLAlchemy for data migration\n\nfrom sqlalchemy import create_engine, MetaData, Table, select  # Import necessary modules\nimport pandas as pd\n\ndef migrate_data():\n    # Connect to the old database\n    engine_old = create_engine('sqlite:///old_database.db')\n    metadata_org = MetaData(bind=engine_old)\n    table_old = Table('users', metadata_org, autoload_with=engine_old)\n\n    # Connect to the new database\n    engine_new = create_engine('sqlite:///new_database.db')\n    metadata_new = MetaData(bind=engine_new)\n    table_new = Table('clients', metadata_new, autoload_with=engine_new)\n\n    # Fetch data from old database\n    with engine_old.connect() as connection:\n        query = select(table_old)\n        result = connection.execute(query)\n        data_old = result.fetchall()\n\n    # Transform and load data to the new database\n    with engine_new.connect() as connection:\n        for row in data_old:\n            insert_stmt = table_new.insert().values(\n                id=row[0],\n                name=row[1],\n                email=row[2]\n            )\n            connection.execute(insert_stmt)\n\nmigrate_data()