# Pseudo-Python with macros\n\n@macro\ndef logging_decorator(func_name):\n    return f"""\ndef {func_name}(*args, **kwargs):\n    print(f"Calling {func_name} with args: {args}, kwargs: {kwargs}")\n    result = original_{func_name}(*args, **kwargs)\n    print(f"{func_name} returned {result}")\n    return result\n"""\n\n# Usage of the macro\n@logging_decorator\ndef my_function(x, y):\n    return x + y\n\nmy_function(5, 3)  # This would automatically be transformed by the macro at compile time