# list_filters.py\n\nfrom django import template\n\nregister = template.Library()\n\n@register.filter(name='join_list')\ndef join_list(value, arg=1):\n    """Formats a list into a string with commas and an optional Oxford comma."""\n    if not value:\n        return ''\n    \n    # Convert the input to a list in case it's another iterable.\n    values = list(value)\n    \n    if len(values) == 0:\n        return ''\n    elif len(values) == 1:\n        return str(values[0])\n    elif len(values) == 2:\n        return f'{values[0]} and {values[1]}'\n    else:\n        if int(arg):\n            # Join with Oxford comma\n            return ', '.join(values[:-1]) + f', and {values[-1]}'\n        else:\n            # Join without Oxford comma\n            return ', '.join(values[:-2]) + f' and {values[-1]}' if len(values) > 2 else f'{values[0]} and {values[1]}'\n\n# Load the filter in your template with {% load list_filters %}