# Using MySQL connector with Python (assuming you have mysql.connector installed)\nimport mysql.connector\n\ndef connect_mysql():\n    try:\n        conn = mysql.connector.connect(\n            host='localhost',\n            user='youruser',\n            password='yourpassword',\n            database='yourdb'\n        )\n        print("MySQL Connection successful")\n    except Exception as e:\n        print(f"Error: {e}")\n    finally:\n        if 'conn' in locals() and conn.is_connected():\n            conn.close()\n\n# Using PyODBC for SQL Server Express (assuming you have pyodbc and a driver installed)\nimport pyodbc\n\ndef connect_sqlserver_express():\n    try:\n        conn = pyodbc.connect(\n            'DRIVER={SQL Server};'\n            'SERVER=localhost;'\n            'DATABASE=yourdb;'\n            'UID=youruser;'\n            'PWD=yourpassword'\n        )\n        print("SQL Server Connection successful")\n    except Exception as e:\n        print(f"Error: {e}")\n    finally:\n        if 'conn' in locals():\n            conn.close()