import tk  # Ensure tkinter is imported as tk or whatever alias you're using\nimport subprocess\n\ndef convertButtonClick():\n    statusBarText.set('Converting...')\n    root.update_idletasks()  # Allow the GUI to update before proceeding\n\n    if inputFileEntry.get() == '' or outputFileEntry.get() == '':\n        statusBarText.set('Invalid Parameters.')\n        return\n\n    try:\n        retcode = subprocess.call(['Program.exe'], shell=True)  # Note the call and correct usage\n    except Exception as e:\n        statusBarText.set(f'Conversion Failed! Error: {e}')\n        return\n\n    if retcode == 0:\n        statusBarText.set('Conversion Successful!')\n    else:\n        statusBarText.set('Conversion Failed!')\n\n# Ensure you have a Tk instance named `root` and that all your widgets (inputFileEntry, outputFileEntry, etc.) are properly defined