# Pseudo code for configuring LDAPMultiPlugins in Plone ZMI\n\nfrom Products.LDAPUserFolder import manage_addLDAPConnection, manage_addLDAPUserFolder\n\n# Add LDAP Connection\nmanage_addLDAPConnection(context,\n                         'ad_connection',\n                         ldap_server='your-ad-server.com',\n                         connection_title='Active Directory Server',\n                         port=389,\n                         login_attr='sAMAccountName')\n\n# Add LDAP User Folder\nmanage_addLDAPUserFolder(context,\n                         'acl_users',\n                         title='Active Directory Users and Groups',\n                         conn_id='ad_connection',\n                         search_base='DC=yourdomain,DC=com',\n                         roles=['Member', 'Authenticated'])