# Example script in Ghidra Python console\nfrom ghidra.program.util import ProgramGenerator\n\nproject = ghidra.getCurrentProject()\nprogram = ghidra.getCurrentProgram()\nif program is None:\n    print("No program loaded")\nelse:\n    print("Analyzing and Decompiling: " + program.getName())\n    decompInterface = DecompilerInterface(program)\n    functions = list(ProgramUtilities.getFunctions(program.getFunctionManager(), True))\n    for function in functions:\n        try:\n            decomplib = decompInterface.decompileFunction(function, 360, None).getDecompiledFunction().getC()\n            print(decomplib.getSource())\n        except Exception as e:\n            print(f"Error processing {function.getName()}: {e}")