# Python to perform a simple file copy with progress bar\nimport shutil, sys\n\ndef copy_with_progress(src, dst):\n    src_size = shutil.disk_usage(src).total\n    with open(src, 'rb') as fsrc, open(dst, 'wb') as fdst:\n        while True:\n            buf = fsrc.read(2**16)\n            if not buf: break\n            fdst.write(buf)\n            sys.stdout.write('\rCopied {:.1%} of {}'.format(fdst.tell() / src_size, dst))\n            sys.stdout.flush()\n    sys.stdout.write('\n')\n\ncopy_with_progress('source.txt', 'destination.txt')