import fitz  # PyMuPDF\n\ndef add_typewritten_text(input_pdf_path, output_pdf_path, text_data, field_positions):\n    # Open the PDF file\n    document = fitz.open(input_pdf_path)\n    \n    # Load a monospaced font to mimic typewriter effect\n    font = "Courier"  # Adjust this if needed\n\n    for page_number, texts in enumerate(text_data):\n        page = document.load_page(page_number)  # Get the specific page\n        for position_id, text_value in texts.items():\n            x0, y0, _, _ = field_positions[position_id]  # Get coordinates for each field\n            # Add text with typewriter font effect\n            page.insert_textbox(fitz.Rect(x0, y0 - 12, x0 + 150, y0), \n                                text_value, \n                                fontsize=12, \n                                fontname=font,\n                                color=(0, 0, 0))\n\n    # Save the modified PDF\n    document.save(output_pdf_path)\n\n# Example usage (you need to add your field coordinates)\nfield_positions = {\n    "field_1": (50, 300),\n    "field_2": (50, 400)\n}\n\ntext_data_per_page = [\n    {"field_1": "John Doe", "field_2": "Example Street 1"},\n    # Add more pages and text as needed\n]\n\nadd_typewritten_text("input_form.pdf", "output_form.pdf", text_data_per_page, field_positions)