# Constants\nblock_size = 8192  # or 4096, depending on your Oracle DB configuration\naverage_row_size = 100  # Adjust this based on column types and sizes\n\n# Variables (example)\nnumber_of_rows = 1000000\noverhead_per_row = 20  # Overhead varies; 20 bytes is a rough average\n\n# Calculations\ntotal_data_size = number_of_rows * (average_row_size + overhead_per_row)\n\n# Account for block size and block header space\nblocks_needed = total_data_size / (block_size - 18)  # Subtract block header\n\nestimated_table_size_bytes = blocks_needed * block_size\n\nprint(f"Estimated Table Size: {estimated_table_size_bytes / (1024**2):.2f} MB")