# Simple python script for checking website security headers\n  import requests\n\n  url = 'https://example.com'\n  response = requests.get(url)\n\n  def check_security_headers(response):\n      expected_headers = ['Strict-Transport-Security', 'Content-Security-Policy', 'X-XSS-Protection']\n      missing_headers = [h for h in expected_headers if h not in response.headers]\n      return missing_headers\n\n  print(check_security_headers(response))