# Simple HTTP Server setup with WebDAV capabilities using Python and wsgidav\n\nfrom wsgidav.wsgidav_app import WsgiDavApp\nfrom wsgidav.fs_dav_provider import FilesystemProvider\nimport logging\n\ndef run():\n    # Configure the logger\n    logging.basicConfig(level=logging.DEBUG)\n    \n    # Create a provider with access to the file system\n    root_folder_path = r"/path/to/your/directory"\n    provider = FilesystemProvider(root_folder_path)\n    \n    # Configuration options for the WebDAV server\n    dav_app = WsgiDavApp(\n        provider=provider,\n        config={\n            "host": "0.0.0.0",\n            "port": 8080,\n            "http_authenticator": {\n                "http_methods": ["GET", "HEAD"],\n                "accept_basic": True, \n                "accept_digest": False,\n            },\n            "verbose": 1,\n        }\n    )\n    \n    # Start the server using wsgiref simple_server\n    print("Starting WebDAV server on http://0.0.0.0:8080")\n    from wsgiref.simple_server import make_server\n    server = make_server('0.0.0.0', 8080, dav_app)\n    try:\n        server.serve_forever()\n    except KeyboardInterrupt:\n        pass\n\nif __name__ == "__main__":\n    run()