# Open files for reading and writing\nwith open('d:/icm_db/users.txt', 'r') as fread, open('d:/icm_db/wp_sql/wp.users.sql', 'w') as fwrite:\n    # Iterate through each line in the input file\n    for line in fread:\n        # Strip any extra whitespace and split by tabs\n        parts = line.strip().split('\t')\n        \n        # Generate SQL INSERT statement\n        sql_insert = f"INSERT INTO `wp_users` (`ID`, `user_login`, `display_name`, `website`, `location`) " \\n                     f"VALUES ('{parts[0]}', '{parts[1]}', '{parts[2]}', '{parts[3]}', '{parts[4]}');\n"\n        \n        # Write the SQL statement to the output file\n        fwrite.write(sql_insert)