# Load the file into a set of words\ndef load_dictionary(file_path):\n    with open(file_path, 'r') as file:\n        # Assuming each word is on a new line\n        english_words = {line.strip() for line in file}\n    return english_words\n\n# Function to check if a word is in the dictionary\ndef is_english_word(word, english_words):\n    return word in english_words\n\n# Example usage\nif __name__ == "__main__":\n    # Load the English words from a file into memory\n    english_words = load_dictionary('path_to_your_file.txt')\n    \n    # Check if a word is an English word\n    test_word = "example"\n    print(is_english_word(test_word, english_words))