import fitz  # PyMuPDF\n\ndef get_title_from_pdf(pdf_path):\n    document = fitz.open(pdf_path)\n    if len(document) == 0:\n        return None\n    page = document.load_page(0)  # Load first page\n    text = page.get_text("text").split('\n')\n    title = text[0].strip() if text else ""\n    document.close()\n    return title\n\n# Example usage:\npdf_file_path = 'path/to/your/paper.pdf'\ntitle = get_title_from_pdf(pdf_file_path)\nprint(f"Extracted Title: {title}")