# Sample data\nList_1 = ["Alice", "Bob", "Charlie"]\nList_2 = [{"age": 25}, {"age": 30}, {"age": 35}]\nList_3 = [{"city": "New York"}, {"city": "Los Angeles"}, {"city": "Chicago"}]\n\n# Initialize the main dictionary\nnested_dict = {}\n\n# Iterate over the lists and construct the nested dictionary\nfor name, details_dict, additional_info in zip(List_1, List_2, List_3):\n    # Update details_dict with additional_info and assign it to the name key\n    nested_dict[name] = {**details_dict, **additional_info}\n\nprint(nested_dict)