# views.py\nfrom django.http import HttpResponse\n\ndef upload_files(request):\n    if request.method == 'POST':\n        files = request.FILES.getlist('file')\n        for f in files:\n            # Process each uploaded file here\n            print(f.name)  # Just an example, you can save the file or do any processing needed.\n            \n            # Example of saving the file to a specific directory\n            with open(f'/path/to/save/{f.name}', 'wb+') as destination:\n                for chunk in f.chunks():\n                    destination.write(chunk)\n\n        return HttpResponse("Files uploaded successfully")\n    else:\n        return HttpResponse("Please upload files through POST")