#!/usr/bin/env python3\n\nimport cgi\n\ndef process_choice.choice):\n    # Here you would continue with your program logic using the chosen option\n    print("Content-type: text/html")\n    print()\n    print(f"<html><body>You chose: {choice}</body></html>")\n\nif __name__ == "__main__":\n    form = cgi.FieldStorage()\n    if "choice" in form:\n        process_choice(form.getvalue('choice'))\n    else:\n        print("Content-type: text/html")\n        print()\n        print("<html><body>No choice made.</body></html>")