# Example using psd-tools in Python\nfrom psd_tools import PSDImage\n\ndef extractLayersFromPsd(psdFilePath):\n    psd = PSDImage.open(psdFilePath)\n    for layer_index, layer in enumerate(psd.descendants()):\n        if not layer.is_group and layer.visible:\n            # Save each visible layer as a PNG\n            output_file_path = f'layer_{layer.name}.png'\n            layer_image = layer.compose()\n            layer_image.save(output_file_path)\n            print(f'Saved: {output_file_path}')\n\n# Example usage\nextractLayersFromPsd('path/to/your/file.psd')