# Simple example in Python to alter LSBs\n   from PIL import Image\n\n   def scramble_lsb(image_path):\n       img = Image.open(image_path)\n       pixels = img.load()\n       width, height = img.size\n\n       for i in range(width):\n           for j in range(height):\n               r, g, b = pixels[i, j]\n               # Alter the LSBs of each color component\n               pixels[i, j] = (r ^ 1, g ^ 1, b ^ 1)\n       save_path = image_path.rsplit('.', 1)[0] + '_scrubbed.' + image_path.rsplit('.', 1)[1]\n       img.save(save_path)\n\n   scramble_lsb('example.tiff')