# Example of a simple migration using Django's ORM (Python)\nfrom django.db import migrations, models\n\nclass Migration(migrations.Migration):\n\n    dependencies = [\n        ('your_app_name', 'previous_migration'),\n    ]\n\n    operations = [\n        migrations.AddField(\n            model_name='yourschema',\n            name='new_field',\n            field=models.CharField(max_length=200),\n        ),\n        # Add raw SQL for RDBMS-specific features\n        migrations.RunSQL("CREATE INDEX CONCURRENTLY my_index ON your_table (column_name);")\n    ]