# Example of a simple operation in GLSL vs CUDA for context\n\n# GLSL Fragment Shader Code\nglsl_code = """\nvoid main() {\n    vec4 color1 = texture(texture0, texCoord);\n    vec4 color2 = texture(texture1, texCoord);\n    gl_FragColor = (color1 + color2) / 2.0;\n}\n"""\n\n# Equivalent CUDA Kernel for element-wise addition of two arrays\ncuda_code = """\n__global__ void addArrays(float* result, float* array1, float* array2, int size) {\n    int idx = blockIdx.x * blockDim.x + threadIdx.x;\n    if(idx < size) {\n        result[idx] = array1[idx] + array2[idx];\n    }\n}\n"""