# room.py\n\nclass CRoom:\n    def __init__(self):\n        self.People = {}\n        self.NextId = 0\n\n    def AddPerson(self, FirstName, SecondName, Gender):\n        Id = self.NextId\n        self.NextId += 1\n\n        # Import CPerson locally to avoid circular import\n        from person import CPerson\n        Person = CPerson(self, FirstName, SecondName, Gender, Id)\n        self.People[Id] = Person\n        return Person\n\n    def FindDoorAndLeave(self, PersonId):\n        del self.People[PersonId]