# Factory Function Example\ndef maker(N):\n    def action(X):\n        return X * N\n    return action\n\ndoubler = maker(2)\nprint(doubler(5))  # Output: 10\n\n\n# Class Equivalent\nclass Multiplier:\n    def __init__(self, N):\n        self.N = N\n    \n    def multiply(self, X):\n        return X * self.N\n\ndoubler_instance = Multiplier(2)\nprint(doubler_instance.multiply(5))  # Output: 10