# Import CherryPy\nimport cherrypy\n\nclass HelloWorld:\n    @cherrypy.expose\n    def index(self):\n        return "Hello World!"\n\nif __name__ == '__main__':\n    # Configure CherryPy to run on top of LightTPD with FastCGI\n    # This requires setting up LightTPD to forward requests via FastCGI\n    cherrypy.config.update({\n        'server.socket_host': '127.0.0.1',\n        'server.socket_port': 8080,\n        'engine.autoreload_on': False,\n        'log.access_file': "access.log",\n        'log.error_file': "site.log"\n    })\n    cherrypy.quickstart(HelloWorld())