# Sample function to normalize line endings\ndef normalize_line_endings(text):\n    # Replace Windows style newlines\n    text = text.replace('\r\n', '\n')\n    # Optionally, convert all other kinds of newline characters to Unix style \n\n    text = text.replace('\r', '\n')\n    return text\n\n# Usage example\nuser_input = "This is a line.\r\nAnd this is another line on Windows.\rBut this could be on old Mac."\nnormalized_text = normalize_line_endings(user_input)\nprint(normalized_text)